  def main(args: Array[String]): Unit = {
    val words = loadWordsMVP()
    println(words.size + " words")
    val start = System.currentTimeMillis()
    val solutions = new Solver(words, 5).findSolutionsPar(Square())
    println(System.currentTimeMillis() - start)
    println(solutions.size + " solutions found")
    val unique = solutions.filter(sq => sq.words(0) < sq.words(1))
    println(unique.size + " unique combinations")
    val results = unique.map(_.horizontalWords.mkString).sorted
    val output = results.mkString("[\"", "\",\"", "\"]")
    println(output == expectedResultsMVP)
  }

  private def loadWordsFromJSON(fileName: String): ArrayBuffer[String] = {
    val solutionsFile = os.pwd / fileName
    val solutionsJson = os.read(solutionsFile)
    val solutionsArray = ujson.read(solutionsJson)
    solutionsArray.arr.map(_.str)
  }

  private def loadWordsMVP(): Seq[String] =
    loadWordsFromJSON("solutions.json").flatMap(_.grouped(5)).distinct.toSeq.sorted

  private def loadWordsMVP2(): Seq[String] =
    loadWordsFromJSON("solutions.json").toSeq
      .flatMap { fiveWords =>
        val horizontalWords = fiveWords.grouped(5).toSeq
        val verticalWords = (0 to 4).map(i => horizontalWords.map(_.charAt(i)).mkString)
        horizontalWords ++ verticalWords
      }
      .distinct
      .sorted

  private def loadWords(): Seq[String] =
    loadWordsFromJSON("words.json").distinct.toSeq.sorted

